import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Typography, List, ListItem, ListItemText, ListItemSecondaryAction, IconButton, Chip, TextField, Box } from '@mui/material';
import { Delete as DeleteIcon, Edit as EditIcon } from '@mui/icons-material';
import { format } from 'date-fns';
import { API_BASE_URL } from '../config';

const TaskList = ({ onEditTask }) => {
  const [tasks, setTasks] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async (query = '') => {
    try {
      let url = `${API_BASE_URL}/api/tasks`;
      if (query) {
        url = `${API_BASE_URL}/api/tasks/search?query=${encodeURIComponent(query)}`;
      }
      const res = await axios.get(url);
      setTasks(res.data);
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };

  const handleDeleteTask = async (id) => {
    try {
      await axios.delete(`${API_BASE_URL}/api/tasks/${id}`);
      fetchTasks(searchQuery);
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  const handleSearch = (event) => {
    const query = event.target.value;
    setSearchQuery(query);
    fetchTasks(query);
  };

  return (
    <div>
      <Typography variant="h2" gutterBottom>Tasks</Typography>
      <Box mb={2}>
        <TextField
          fullWidth
          label="Search tasks"
          variant="outlined"
          value={searchQuery}
          onChange={handleSearch}
        />
      </Box>
      <List>
        {tasks.map(task => (
          <ListItem key={task.id} divider>
            <ListItemText
              primary={task.title}
              secondary={
                <>
                  <Typography component="span" variant="body2" color="textPrimary">
                    {task.description}
                  </Typography>
                  <br />
                  <Chip 
                    label={`Due: ${format(new Date(task.dueDate), 'PP')}`} 
                    size="small" 
                    color="primary"
                  />
                </>
              }
            />
            <ListItemSecondaryAction>
              <IconButton edge="end" aria-label="edit" onClick={() => onEditTask(task)}>
                <EditIcon />
              </IconButton>
              <IconButton edge="end" aria-label="delete" onClick={() => handleDeleteTask(task.id)}>
                <DeleteIcon />
              </IconButton>
            </ListItemSecondaryAction>
          </ListItem>
        ))}
      </List>
    </div>
  );
};

export default TaskList;
